import React, { Component } from 'react'
import FileBase64 from 'react-file-base64'
import { addImage, imgurImage } from '../../services/apiCalls'

const IMGUR_BUTTON = 'Upload To Imgur'
const IMGUR_LOADING = 'Uploading...'

export default class AddImage extends Component {
  constructor(props) {
    super(props)

    this.state = {
      imgFile: null,
      inputURL: '',
      errMsg: '',
      imgurBtn: IMGUR_BUTTON
    }
  }

  handleChange = (e, stateName) => {
    // sets e.target.value to whatever state is named
    this.setState({
      [stateName]: e.target.value 
    })
  }

  handleImgur = async () => {
    if (this.state.imgurBtn === IMGUR_BUTTON) {
      // change the button state and then api call 
      this.setState({
        imgurBtn: IMGUR_LOADING
      })
      try {
        const response = await imgurImage({
          base64: this.state.imgFile.base64.split(',')[1],
          name: this.state.imgFile.file.name 
        })
      } catch (error) {
        console.log(error.message)
        this.setState({
          errMsg: error.message,
          imgurBtn: IMGUR_BUTTON
        })
      }
    }
  }



  render() {
    return (
      <div className="AddImage">
        {
          this.state.errMsg ? <h3>{this.state.errMsg}</h3> : null 
        }

        <FileBase64
          multiple={false}
          onDone={e => this.setState({imgFile: e})}
        />

        <label htmlFor="inputURL">Enter Or Edit Image URL</label>
        <input 
          type="text"
          value={this.state.inputURL}
          onChange={e => this.handleChange(e, 'inputURL')}
          />
      </div>
    )
  }
}
