import React, { Component } from 'react'
import { withRouter } from 'react-router-dom'
import { getProject } from '../../services/apiCalls'

class ViewProject extends Component {
  constructor(props) {
    super(props)

    this.state = {
      name: '',
      location: '',
      description: ''
    }
  }

  async componentDidMount() {
    try {
      const response = await getProject(this.props.match.params.id)

      this.setState({
        name: response.name,
        location: response.location,
        description: response.description 
      })
    } catch (error) {
      console.log(error.message)
    }
  }

  render() {
    if (!this.state.name) {
      return (
        <div className="Page">
          <h1>No Project Info Found</h1>
        </div>
      )
    } else {
      return (
        <div className="Page">
          <h1>Project Site</h1>
          <h2>{this.state.name}</h2>
          <h4>Location: </h4> <p>{this.state.location ? this.state.location : 'Not given'}</p>
          {
            this.state.description ? <div><h4>Description</h4><p>{this.state.description}</p></div> : null  
          }
        </div>
      )
    }
  }
}

export default withRouter(ViewProject)
