import React, { Component } from 'react'
import { withRouter, Link } from 'react-router-dom'
import { getProject } from '../../services/apiCalls'

class ViewProject extends Component {
  constructor(props) {
    super(props)

    this.state = {
      project: null 
    }
  }

  async componentDidMount() {
    try {
      const response = await getProject(this.props.match.params.id)

      this.setState({
        project: response 
      })
    } catch (error) {
      console.log(error.message)
    }
  }

  render() {
    if (!this.state.project) {
      return (
        <div className="Page">
          <h1>No Project Info Found</h1>
        </div>
      )
    } else {
      return (
        <div className="Page">
          <h1>Project Site</h1>
          <h2>{this.state.project.name}</h2>
          <h4>Location: </h4> <p>{this.state.project.location ? this.state.project.location : 'Not given'}</p>
          {
            this.state.project.description ? <div><h4>Description</h4><p>{this.state.project.description}</p></div> : null  
          }

          { this.props.userID === this.state.project.user ? <Link to={`/projects/${this.props.match.params.id}/addentry`}><h2>Create An Entry</h2></Link> : null }

          <ul>
            {
              this.state.project.entries.map((entry, ind) => {
                return (<li key={ind}>
                  <Link to={`/entries/${entry._id}`}>{entry.notes}</Link>
                </li>)
              })
            }
          </ul>
        </div>
      )
    }
  }
}

export default withRouter(ViewProject)
