import React, { Component } from 'react'
import { withRouter, Link } from 'react-router-dom'
import { getProject } from '../../services/apiCalls'

class ViewProject extends Component {
  constructor(props) {
    super(props)

    this.state = {
      name: '',
      location: '',
      description: '',
      entries: [],
      userID
    }
  }

  async componentDidMount() {
    try {
      const response = await getProject(this.props.match.params.id)

      this.setState({
        name: response.name,
        location: response.location,
        description: response.description,
        entries: response.entries,
        userID: response.user.toString() 
      })
    } catch (error) {
      console.log(error.message)
    }
  }

  render() {
    if (!this.state.name) {
      return (
        <div className="Page">
          <h1>No Project Info Found</h1>
        </div>
      )
    } else {
      return (
        <div className="Page">
          <h1>Project Site</h1>
          <h2>{this.state.name}</h2>
          <h4>Location: </h4> <p>{this.state.location ? this.state.location : 'Not given'}</p>
          {
            this.state.description ? <div><h4>Description</h4><p>{this.state.description}</p></div> : null  
          }

          { this.state.project && this.props.userID.toString() === this.state.project.user.toString() ? <Link to={`/projects/${this.props.match.params.id}/createentry`}><h2>Create An Entry</h2></Link> : null }

          <ul>
            {
              this.state.entries.map((entry, ind) => {
                return (<li key={ind}>
                  <Link>{entry.notes}</Link>
                </li>)
              })
            }
          </ul>
        </div>
      )
    }
  }
}

export default withRouter(ViewProject)
